import clsx from 'clsx';

import Link from '../Link';

const paddingClasses = {
  md: 'px-3.5 py-2.5',
  lg: 'px-4 py-2.5',
  xl: 'px-5 py-3',
  '2xl': 'px-6 py-4',
};

// We need this because secondary button has border
const secondaryVariantPaddingClasses = {
  md: 'px-[13px] py-[9px]',
  lg: 'px-[15px] py-[9px]',
  xl: 'px-[19px] py-[11px]',
  '2xl': 'px-[23px] py-[15px]',
};

const fontSizeClasses = {
  md: 'text-sm',
  lg: 'text-base',
  xl: 'text-base',
  '2xl': 'text-lg',
};

const spacingClasses = {
  md: 'gap-x-1.5',
  lg: 'gap-x-2',
  xl: 'gap-x-2',
  '2xl': 'gap-x-3',
};

const heightClasses = {
  md: 'h-10',
  lg: 'h-11',
  xl: 'h-12',
  '2xl': 'h-15',
};

const iconOnlySizeClasses = {
  md: 'size-10',
  lg: 'size-11',
  xl: 'size-12',
  '2xl': 'size-14',
};

const iconSizeClasses = {
  md: 'size-5',
  lg: 'size-5',
  xl: 'size-5',
  '2xl': 'size-6',
};

const variantClasses = {
  primary: clsx(
    'border-none',
    'bg-indigo-700',
    'shadow-custom',
    'text-white',
    'hover:bg-indigo-800 focus:bg-indigo-800',
  ),
  secondary: clsx(
    'border border-neutral-200',
    'bg-white',
    'shadow-custom',
    'text-neutral-900',
    'hover:bg-neutral-50 focus:bg-neutral-50',
  ),
  tertiary: clsx(
    'border-none',
    'text-indigo-700',
    'hover:bg-neutral-50 focus:bg-neutral-50',
  ),
  danger: clsx(
    'border-none',
    'bg-red-600',
    'text-white',
    'hover:bg-red-700 focus:bg-red-700 focus:outline-none focus-visible:ring-4 focus-visible:ring-red-600/[.12]',
  ),
  link: clsx(
    'text-indigo-700',
    'hover:text-indigo-800 focus:text-indigo-800',
    'rounded focus:outline-none focus-visible:ring-4 focus-visible:ring-indigo-600/[.12]',
  ),
  'gray-link': clsx(
    'text-neutral-600',
    'hover:text-neutral-900 focus:text-neutral-900',
    'rounded focus:outline-none focus-visible:ring-4 focus-visible:ring-indigo-600/[.12]',
    'px-0.5',
  ),
};

const variantDisabledClasses = {
  primary: clsx(
    'disabled:bg-neutral-100',
    'disabled:text-neutral-400',
    'disabled:shadow-none',
  ),
  secondary: clsx(
    'disabled:bg-neutral-100',
    'disabled:text-neutral-400',
    'disabled:shadow-none',
  ),
  tertiary: clsx('disabled:bg-none', 'disabled:text-neutral-400'),
  danger: clsx('disabled:bg-none', 'disabled:text-neutral-400'),
  link: clsx('disabled:text-neutral-400'),
  'gray-link': clsx('disabled:text-neutral-400'),
};

const Button = ({
  label,
  className,
  isDisabled,
  startIcon: StartIcon,
  endIcon: EndIcon,
  isLabelHidden,
  size = 'md',
  variant = 'primary',
  iconClassName,
  href,
  ...props
}) => {
  const isLinkVariant = ['link', 'gray-link'].includes(variant);

  const commonClasses = clsx(
    'inline-flex items-center justify-center rounded font-medium outline-none cursor-pointer',
    'focus:outline-none focus-visible:ring-4 focus-visible:ring-indigo-600/[.12]',
    'transition-colors',
    'text-nowrap',
    !isLinkVariant && heightClasses[size],
    !isLinkVariant &&
      (variant === 'secondary'
        ? secondaryVariantPaddingClasses[size]
        : paddingClasses[size]),
    fontSizeClasses[size],
    spacingClasses[size],
    isLabelHidden && iconOnlySizeClasses[size],
    variantClasses[variant],
    variantDisabledClasses[variant],
    isDisabled && 'pointer-events-none',
  );

  if (href) {
    return (
      <Link
        to={href}
        variant="unstyled"
        disabled={isDisabled}
        className={clsx(commonClasses, className)}
        {...props}>
        {StartIcon && (
          <StartIcon
            className={clsx('size-5 shrink-0 p-0.5', iconClassName)}
            aria-hidden="true"
          />
        )}
        {label}
        {EndIcon && (
          <EndIcon
            className={clsx('size-5 shrink-0 p-0.5', iconClassName)}
            aria-hidden="true"
          />
        )}
      </Link>
    );
  }

  const children = isLabelHidden ? (
    (
      <StartIcon
        className={clsx('shrink-0', iconSizeClasses[size], iconClassName)}
        aria-hidden="true"
      />
    ) || (
      <EndIcon
        className={clsx('shrink-0', iconSizeClasses[size], iconClassName)}
        aria-hidden="true"
      />
    )
  ) : (
    <>
      {StartIcon && (
        <StartIcon
          className={clsx('size-5 shrink-0 p-0.5', iconClassName)}
          aria-hidden="true"
        />
      )}
      {label}
      {EndIcon && (
        <EndIcon
          className={clsx('size-5 shrink-0 p-0.5', iconClassName)}
          aria-hidden="true"
        />
      )}
    </>
  );

  return (
    <button
      type="button"
      className={clsx(commonClasses, className)}
      disabled={isDisabled}
      {...props}>
      {children}
    </button>
  );
};

export default Button;

/src/components/ui/ConfirmModal/ConfirmM.code:
import clsx from 'clsx';
import { useEffect } from 'react';
import { createPortal } from 'react-dom';
import { RiCloseLine } from 'react-icons/ri';

import Button from '../Button';

const ConfirmModal = ({
  isOpen,
  onClose,
  onAction,
  title,
  description,
  children,
  primaryActionLabel,
  secondaryActionLabel,
  actionBtnSize = 'lg',
  className,
}) => {
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isOpen]);

  if (!isOpen) return null;

  return createPortal(
    <div
      className={clsx(
        'z-modal fixed  inset-0',
        'bg-neutral-950 bg-opacity-70',
        'flex items-center justify-center',
      )}
      role="dialog"
      aria-modal="true">
      <div
        className={clsx(
          'rounded-lg bg-white',
          'w-[343px]',
          'p-6',
          'flex flex-col gap-8',
          className,
        )}>
        {children ? (
          children
        ) : (
          <div className="flex flex-col gap-1">
            <div className={clsx('flex items-center justify-between gap-4')}>
              <div className="text-lg font-semibold">{title}</div>
              <button
                aria-label="Close modal"
                className="text-xl font-semibold text-black"
                onClick={onClose}>
                <RiCloseLine className="size-6" />
              </button>
            </div>

            <p className="text-sm text-neutral-600">{description}</p>
          </div>
        )}
        <div className={clsx('flex gap-3')}>
          {secondaryActionLabel && (
            <Button
              label={secondaryActionLabel}
              variant="secondary"
              size={actionBtnSize}
              className="flex-1"
              onClick={onClose}
            />
          )}
          <Button
            label={primaryActionLabel}
            size={actionBtnSize}
            className="flex-1"
            onClick={onAction}
          />
        </div>
      </div>
    </div>,
    document.body,
  );
};

export default ConfirmModal;

/src/components/ui/Link/Link.jsx.code:
import clsx from 'clsx';
import { NavLink, Link as RouterLink } from 'react-router-dom';

const linkVariantClasses = {
  primary: clsx(
    'text-indigo-700',
    'hover:text-indigo-800 focus:text-indigo-800',
    'rounded focus:outline-none focus-visible:ring-4 focus-visible:ring-indigo-600/[.12]',
    'px-0.5',
  ),
  gray: clsx(
    'text-neutral-600',
    'hover:text-neutral-900 focus:text-neutral-900',
    'rounded focus:outline-none focus-visible:ring-4 focus-visible:ring-indigo-600/[.12]',
    'px-0.5',
  ),
  unstyled: '',
};

const activeLinkClasses = {
  primary: 'text-indigo-800',
  gray: 'text-neutral-900',
  unstyled: '',
};

const Link = ({
  children,
  disabled,
  className,
  type = 'default',
  variant = 'primary',
  ...props
}) => {
  const commonClassName = clsx(
    'font-medium rounded',
    linkVariantClasses[variant],
    {
      'pointer-events-none text-neutral-400': disabled,
    },
    className,
  );

  if (type === 'nav') {
    return (
      <NavLink
        {...props}
        className={({ isActive }) =>
          clsx(commonClassName, isActive && activeLinkClasses[variant])
        }>
        {children}
      </NavLink>
    );
  }
  return (
    <RouterLink {...props} className={clsx(commonClassName)}>
      {children}
    </RouterLink>
  );
};

export default Link;

