import clsx from 'clsx';

import Rating from 'src/components/ui/Rating';
import ProgressBar from 'src/components/ui/ProgressBar';
import Button from 'src/components/ui/Button';

import { useProductReviewsContext } from './ProductReviewsContext';

const OverallRating = () => {
  const { aggregateRating, onRatingSelect, selectedRating } =
    useProductReviewsContext();
  const hasReviews = !!aggregateRating.total;

  const ratingData = [
    {
      name: 'Excellent',
      color: '#16A34A',
      value: aggregateRating.counts.find((item) => item.rating === 5).count,
      rating: 5,
    },
    {
      name: 'Good',
      color: '#22C55E',
      value: aggregateRating.counts.find((item) => item.rating === 4).count,
      rating: 4,
    },
    {
      name: 'Average',
      color: '#FDE047',
      value: aggregateRating.counts.find((item) => item.rating === 3).count,
      rating: 3,
    },
    {
      name: 'Below Average',
      color: '#EAB308',
      value: aggregateRating.counts.find((item) => item.rating === 2).count,
      rating: 2,
    },
    {
      name: 'Poor',
      color: '#FACC15',
      value: aggregateRating.counts.find((item) => item.rating === 1).count,
      rating: 1,
    },
  ];
  return (
    <div className={clsx('flex flex-col gap-6')}>
      <div className="flex flex-col gap-2">
        <div className="text-xl font-semibold text-neutral-900">
          Overall Rating
        </div>
        <div className="flex items-center gap-2">
          <span className="text-base font-semibold text-neutral-900">
            {aggregateRating.rating}
          </span>
          <Rating value={4.5} className="gap-2" />
          {hasReviews && (
            <span className="text-nowrap text-sm text-neutral-600">
              Based on {aggregateRating.total} reviews
            </span>
          )}
        </div>
      </div>

      <fieldset className={clsx('flex flex-col gap-4', 'py-4')}>
        <legend id="rating-filter" className="sr-only">
          Filter by Rating
        </legend>
        {ratingData.map(({ name, color, value, rating }) => {
          const ratingPercentage =
            aggregateRating.total === 0
              ? 0
              : Math.floor((value / aggregateRating.total) * 100);

          return (
            <button
              className={clsx(
                'flex items-center gap-2 self-stretch',
                'text-neutral-600 disabled:text-neutral-400',
                'cursor-pointer disabled:pointer-events-none',
              )}
              key={rating}
              disabled={!hasReviews}
              aria-pressed={selectedRating === rating ? 'true' : 'false'}
              aria-label={`Filter by ${rating} stars`}
              onClick={() => onRatingSelect(rating)}>
              <span
                className={clsx(
                  'w-[120px]',
                  'px-0.5',
                  'text-left font-medium',
                )}>
                {name}
              </span>
              <ProgressBar value={ratingPercentage} color={color} />
              <span className="w-[42px] text-right">{ratingPercentage}%</span>
            </button>
          );
        })}
      </fieldset>

      <div className="flex justify-center gap-6">
        {selectedRating && (
          <div className="flex-1">
            <Button
              label="Clear Filter"
              variant="tertiary"
              size="xl"
              className="w-full"
              onClick={() => onRatingSelect(null)}
            />
          </div>
        )}
        <div className={clsx(selectedRating ? 'flex-1' : 'w-[153px]')}>
          <Button
            label="Write a review"
            variant="secondary"
            size="xl"
            className="w-full"
          />
        </div>
      </div>
    </div>
  );
};

export default OverallRating;

/src/components/Reviews/ProductReviewsCo.code:
import { useParams } from 'react-router-dom';
import { useMediaQuery } from 'usehooks-ts';

import {
  createContext,
  useContext,
  useState,
  useEffect,
  useCallback,
  useMemo,
} from 'react';

const ProductReviewsContext = createContext();

export const useProductReviewsContext = () => useContext(ProductReviewsContext);

const ProductReviewsContextProvider = ({ children }) => {
  const { productId } = useParams();
  const isDesktopView = useMediaQuery('(min-width: 1024px)');
  const [reviews, setReviews] = useState([]);
  const [isInitialLoading, setIsInitialLoading] = useState(true);
  const [isFetchingMore, setIsFetchingMore] = useState(false);

  const [currentPage, setCurrentPage] = useState(1);
  const [pagination, setPagination] = useState({
    hasMore: false,
    total: 0,
  });
  const [aggregateRating, setAggregateRating] = useState(null);
  const [selectedRating, setSelectedRating] = useState(null);
  const limit = isDesktopView ? 12 : 10;

  const getReviews = useCallback(
    async (initialFetching = false) => {
      if (initialFetching) {
        setIsInitialLoading(true);
      } else {
        setIsFetchingMore(true);
      }

      const response = await fetch(
        `https://www.greatfrontend.com/api/projects/challenges/e-commerce/products/${productId}/reviews?page=${currentPage}&per_page=${limit}${
          selectedRating ? `&rating=${selectedRating}` : ''
        }`,
      );

      const result = await response.json();

      if (result) {
        setReviews(
          currentPage === 1 ? result.data : (prev) => [...prev, ...result.data],
        );
        setAggregateRating(result.aggregate);
        setPagination({
          hasMore: result.pagination.has_more,
          total: result.pagination.total,
        });
        setCurrentPage(result.pagination.page);
      }
      setIsInitialLoading(false);
      setIsFetchingMore(false);
    },
    [currentPage, limit, selectedRating, productId],
  );

  useEffect(() => {
    getReviews(isInitialLoading);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentPage, selectedRating]);

  const loadMoreReviews = useCallback(() => {
    if (pagination.hasMore) {
      setCurrentPage((prev) => prev + 1);
    }
  }, [pagination.hasMore]);

  const onRatingSelect = useCallback((value) => {
    setSelectedRating(value);
    setCurrentPage(1);
  }, []);

  const value = useMemo(() => {
    return {
      reviews,
      pagination,
      isInitialLoading,
      isFetchingMore,
      currentPage,
      aggregateRating,
      selectedRating,
      loadMoreReviews,
      onRatingSelect,
    };
  }, [
    reviews,
    pagination,
    isInitialLoading,
    isFetchingMore,
    currentPage,
    aggregateRating,
    selectedRating,
    loadMoreReviews,
    onRatingSelect,
  ]);

  return (
    <ProductReviewsContext.Provider value={value}>
      {children}
    </ProductReviewsContext.Provider>
  );
};

export default ProductReviewsContextProvider;

