  md: 'M',
  lg: 'L',
  xl: 'XL',
};

const AvailableSizes = () => {
  const { selectedSize, setSelectedSize, selectedColor, product } =
    useProductDetailsContext();
  const { sizes } = product;
  const unavailableSizes = getUnavailableSizes({
    product,
    color: selectedColor,
  });

  return (
