  );

  return (
    <button
      className={clsx(
        commonClasses,
        heightClasses[size],
        variant === 'secondary'
          ? secondaryVariantPaddingClasses[size]
          : paddingClasses[size],
        fontSizeClasses[size],
        spacingClasses[size],
        isLabelHidden && iconOnlySizeClasses[size],
        variantClasses[variant],
        variantDisabledClasses[variant],
        isDisabled && 'pointer-events-none',
        className,
      )}
      disabled={isDisabled}
      {...props}>
      {children}
    </button>
  );
};

