/* Put any custom CSS styles here.  */
/* Be sure to delete the <style> tag in index.html if you want to use this stylesheet. */
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family:
    'Noto Sans',
    system-ui,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Roboto,
    Oxygen,
    Ubuntu,
    Cantarell,
    'Open Sans',
    'Helvetica Neue',
    sans-serif;
  min-height: 100vh;
  background: linear-gradient(147.52deg, #f9fafb 8.89%, #d2d6db 100.48%);
}

.credits {
  color: #777;
  position: fixed;
  bottom: 24px;
  left: 24px;
  font-size: 11px;
  text-align: center;
}

.credits a {
  color: #000;
  font-weight: bold;
}

.container {
  display: flex;
  justify-content: center;
  align-items: flex-start;
  margin: 0 auto;
  min-height: 100vh;
  padding-top: 200px;
  padding-left: 17.5px;
  padding-right: 17.5px;
}

.profile-card {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 23px 15px;
  gap: 40px;

  width: 340px;

  background: #ffffff;

  border: 1px solid #e6e6e6;
  border-radius: 8px;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
}

.profile-card__author {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 24px;
}

.profile-card__author__image {
  width: 64px;
}

.profile-card__author__metadata {
  margin-top: 1px;
  display: flex;
  flex-direction: column;
  gap: 3px;
  align-items: center;
}

.profile-card__author__metadata__name {
  font-weight: 500;
  font-size: 20px;
  line-height: 28px;
  text-align: center;

  color: #171717;
}

.profile-card__author__metadata__designation {
  font-size: 14px;
  line-height: 20px;
  font-weight: 400;
  text-align: center;
  color: #525252;
}

.profile-card__author__bio {
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  color: #525252;
}

.profile-card__author__actions {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 24px;
  width: 100%;
}

.btn {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 14px;
  height: 44px;
  box-shadow:
    0px 1px 3px rgba(0, 0, 0, 0.1),
    0px 1px 2px rgba(0, 0, 0, 0.06);
  border-radius: 4px;

  border: none;
  outline: none;
  font-family: inherit;
  font-style: normal;
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  cursor: pointer;
  width: 100%;
  white-space: noWrap;
}
.btn:focus {
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}
.btn:disabled {
  pointer-events: none;
  background: #f5f5f5;
  color: #a3a3a3;
  box-shadow: none;
}
.btn--primary {
  background: #4338ca;
  color: #ffffff;
}

.profile-card__author__actions__socials {
  display: flex;
  gap: 16px;
}

.social-btn {
  width: 36px;
  height: 36px;

  display: flex;
  align-items: center;
  justify-content: center;

  border: none;
  border-radius: 4px;

  cursor: pointer;
  outline: none;

  color: #4338ca;
}

.social-btn:hover {
  background-color: #fafafa;
}

.social-btn:focus {
  background-color: #fafafa;
  box-shadow: 0px 0px 0px 4px rgba(68, 76, 231, 0.12);
}

.social-btn:disabled {
  pointer-events: none;
}
