  if (!node) {
    return node;
  }

  // Dictionary to save the visited node and it's respective clone
  // as key and value respectively. This helps to avoid cycles
  let visited: Map<GraphNode, GraphNode> = new Map();

  // Put the first node in the queue
  let queue: GraphNode[] = [];
