    const orig = [1, 2, 3];
    const res = orig.myConcat([4, 5, 6]);
    expect(res).toStrictEqual([1, 2, 3, 4, 5, 6]);

    orig.push(4);
    expect(res).toStrictEqual([1, 2, 3, 4, 5, 6]);
  });
});

