          { foo: 'bar', item: [1, 2, { baz: 'baz' }], id: 1 },
        ),
      ).toEqual(false);
    });

    test('null-ish', () => {
      expect(
        deepEqual({ foo: null, baz: 'baz' }, { bar: 'bar', baz: 'baz' }),
      ).toEqual(false);
      expect(
        deepEqual({ foo: null, bar: 'baz' }, { foo: null, bar: 'baz' }),
      ).toEqual(true);
    });
  });
});

