    expect(
      dropRightWhile([1, 2, 3, 4, 5], (value, _index, _array) => value > 3),
    ).toEqual([1, 2, 3]);
  });

  test('predicate always returns true', () => {
    expect(
      dropRightWhile([1, 2, 3, 4, 5], (value, _index, _array) => value < 6),
    ).toEqual([]);
  });

