    expect(
      dropRightWhile([1, 2, 3, 4, 5], (value, _index, _array) => value < 0),
    ).toEqual([1, 2, 3, 4, 5]);
  });

  test('should stop dropping once predicate is false', () => {
    expect(
      dropRightWhile([1, 6, 3, 7, 8], (value, _index, _array) => value > 5),
    ).toEqual([1, 6, 3]);
  });

