    checkResults(expected, divs);
  });

  test('non-existent tags', () => {
    const document = new DOMParser().parseFromString(
      `<div>
        <span>Span</span>
        <p>Paragraph</p>
        <div></div>
      </div>`,
      'text/html',
    );

    const imgs = getElementsByTagName(document.body, 'img');
