    const arr2 = [
      { x: 2, y: 3 },
      { x: 4, y: 5 },
      { x: 5, y: 6 },
    ];

    const comparator = (
      a: { x: number; y: number },
      b: { x: number; y: number },
    ) => a.x === b.x && a.y === b.y;

    const actual = intersectionWith(comparator, arr1, arr2);
    const expected = [{ x: 2, y: 3 }];

    expect(actual).toEqual(expected);
  });

  test('multiple arrays using a comparator', () => {
    const arr1 = [
      { x: 1, y: 2 },
      { x: 2, y: 3 },
      { x: 3, y: 4 },
    ];
    const arr2 = [
      { x: 2, y: 3 },
      { x: 4, y: 5 },
