    const onced = once(() => {});
    expect(onced).toBeInstanceOf(Function);
  });

  test('only run once', () => {
    let i = 0;
    const onced = once(() => ++i);

    onced();
    onced();
    expect(i).toBe(1);
  });

  test('returns the value of the first invocation', () => {
