type AsyncState<T> =
  | { status: 'loading' }
  | { status: 'success'; data: T }
  | { status: 'error'; error: Error };

export default function useQuery<T>(
  fn: () => Promise<T>,
  deps: DependencyList = [],
): AsyncState<T> {
  const [state, setState] = useState<AsyncState<T>>({
    status: 'loading',
  });

  useEffect(() => {
    let ignore = false;

    setState({ status: 'loading' });

    fn()
      .then((data) => {
        if (ignore) {
          return;
        }

        setState({ status: 'success', data });
      })
      .catch((error) => {
        if (ignore) {
          return;
        }

        setState({ status: 'error', error });
      });

    return () => {
      ignore = true;
    };
  }, deps);

  return state;
}
