  return strings.reduce((result, string, i) => {
    const value = values[i - 1];
    return (
      result +
      (value
        ? String(value)
            .replace(/&/g, '&amp;')
            .replace(/</g, '&lt;')
            .replace(/>/g, '&gt;')
        : '') +
      string
    );
  });
}

