  return (
    <div role="alert">
      <p>\u51FA\u9519\u4E86\uFF1A</p>
      <pre style={{ color: 'red' }}>{error.message}</pre>
      <button onClick={resetErrorBoundary}>\u91CD\u8BD5</button>
    </div>
  );
}

function App() {
  return (
    <ErrorBoundary
      FallbackComponent={ErrorFallback}
      onReset={() => {
        // \u91CD\u7F6E\u60A8\u7684\u5E94\u7528\u7A0B\u5E8F\u7684\u72B6\u6001
