`,(0,e.jsx)(n.pre,{children:(0,e.jsx)(n.code,{className:"language-jsx",children:`import React, { useState } from 'react';

function Counter() {
  // \u4F7F\u7528 useState \u94A9\u5B50\u5B9A\u4E49\u4E00\u4E2A\u72B6\u6001\u53D8\u91CF
  const [count, setCount] = useState(0);

  // \u6B64 handleClick \u51FD\u6570\u662F\u4E00\u4E2A\u95ED\u5305
  function handleClick() {
    // \u5B83\u53EF\u4EE5\u8BBF\u95EE 'count' \u72B6\u6001\u53D8\u91CF
    setCount(count + 1);
  }

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={handleClick}>Increment</button>
    </div>
  );
}

function App() {
  return (
    <div>
      <h1>Counter App</h1>
      <Counter />
    </div>
  );
}

export default App;
