import { enableProdMode } from '@angular/core';
import { platformBrowserDynamic } from '@angular/platform-browser-dynamic';

import { AppModule } from './app/app.module';

enableProdMode();
platformBrowserDynamic()
  .bootstrapModule(AppModule)
  .catch((err) => console.error(err));

/src/styles.css.code:
body {
  font-family: sans-serif;
}

.wrapper {
  align-items: center;
  display: flex;
  flex-direction: column;
  gap: 48px;
  padding-top: 16px;
}

.dice-form {
  display: flex;
  align-items: end;
  gap: 16px;
}

.dice-form label {
  display: block;
  font-size: 12px;
  margin-bottom: 8px;
}

.dice-form input {
  width: 100%;
}

.dice-list {
  background-color: #eaeaea;
  border-radius: 24px;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 16px;
  flex-wrap: wrap;
  padding: 16px;
}

.dice {
  --dice-size: 64px;

  width: var(--dice-size);
  height: var(--dice-size);

  background-color: #fff;
  box-sizing: border-box;
  border: 1px solid #000;
  border-radius: 8px;
  padding: 8px;
}

.dots {
  position: relative;
  height: 100%;
}

.dot {
  --dot-size: 12px;

  position: absolute;
  width: var(--dot-size);
  height: var(--dot-size);
  background-color: #000;
  border-radius: 100%;
}

.dot--center {
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

.dot--top-left {
  top: 0;
  left: 0;
}

.dot--top-right {
  top: 0;
  right: 0;
}

.dot--bottom-left {
  bottom: 0;
  left: 0;
}

.dot--bottom-right {
  bottom: 0;
  right: 0;
}

.dot--center-left {
  top: 50%;
  transform: translateY(-50%);
  left: 0;
}

.dot--center-right {
  top: 50%;
  transform: translateY(-50%);
  right: 0;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  border: 0;
}

