  font-weight: bold;
  color: #ff6600;
  margin-bottom: 24px;
}

.jobs {
  display: grid;
  row-gap: 16px;
}

.post {
  background-color: #fff;
  border: 1px solid #dcdcdc;
  border-radius: 4px;
  padding: 16px;
  display: grid;
  row-gap: 8px;
