import { createPortal } from 'react-dom';

export default function ModalDialog({
  children,
  open = false,
  title,
  onClose,
}: Readonly<{
  children: React.ReactNode;
  open?: boolean;
  title: string;
  onClose: () => void;
}>) {
  if (!open) {
    return null;
  }

  return createPortal(
    <div className="modal-overlay">
      <div className="modal">
        <h1 className="modal-title">{title}</h1>
        <div>{children}</div>
        <button onClick={onClose}>Close</button>
      </div>
    </div>,
    document.body,
  );
}

